const color = [
  "#66CC33", 
  "#33CC99",
  "#33CCFF", 
  '#00CCFF', 
  '#CC6600', 
  '#FF3333', 
  '#99FFFF',
  "#00CC00",
  '#CCFFFF',
  '#CCCC33',
  '#FF66CC',
  '#FF3300'
];
const chalk = require("chalk");
module.exports = function (data) {
  return console.log(chalk.hex(color[Math.floor(Math.random() * color.length)])(data))
}